import ArrayLikeWritable from "./ArrayLikeWritable";
/**
 * Replaces values of an array across a range of indexes.
 * @param array
 * @param value
 * @param start
 * @param stop
 */
export default function updateRange<T>(array: ArrayLikeWritable<T>, value: T, start?: number, stop?: number): void;
